  let [medDesc, setMedDesc] = useState<string>("");
  let [longDesc, setLongDesc] = useState<string>("");

  const locate = useLocation();

  useEffect(() => {
    // setText("Select total number of sets");
    // e.g. "/customs?"
    const setPage = locate.pathname.split("?time")[1];
    console.log(locate.pathname);
    console.log(setPage);
    if (setPage === undefined) {
      setShortDesc("30 second per set");
      setMedDesc("40 second per set");
      setLongDesc("50 second per set");
    } else if (setPage !== undefined) {
      setShortDesc("4 sets");
      setMedDesc("8 sets");
      setLongDesc("12 sets");
    }
  }, []);

  // pass to the timer -- & store in backend things?
