        <Blocks location="/customs?time=50" margin="0em auto 0em auto" value={"50"} text="Long" description={longDesc} setText={setText} />
        <Blocks location="/customs?time=custom" margin="0em auto 0em auto" value={"custom"} text="Custom" description="? second per set" setText={setText} />
      </div>
    </>
  );
}

export default Customs;
