import { hasSelectionSupport } from '@testing-library/user-event/dist/utils';
import React, { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogTitle,
  DialogContentText,
  TextField,
  DialogActions,
  Button
} from '@mui/material';
import useSound from 'use-sound';
import start from './timer-start.mp3';

function App() {
  const [num, setNum] = useState(3);
  const [work, setWork] = useState(40);
  const [rest, setRest] = useState(10);

  const [totalTime, setTotalTime] = useState(40);
  const [minutes, setMinutes] = useState("00");
  const [seconds, setSeconds] = useState("40");

  const [open, setOpen] = useState(false);
  const [playSound] = useSound(start); // four seconds remaining

  const secondsToMinutes = (sec) => {
    const mins = Math.floor(sec / 60);
    const secs = Math.ceil(sec % 60);

    return {
      "mins": mins,
      "secs": secs
    }

  }

  const startTimer = () => {
    playSound();
    // disable button
    setTimeout(() => {
      const grabSeconds = Number(seconds); // need to make sure padding is done as well
      setSeconds(grabSeconds - 1);
    }, 4000);
  }

  useState(() => {
    const grabSeconds = Number(seconds);
    setSeconds(grabSeconds - 1);
  }, [seconds]);

  const openModal = () => {
    setOpen(true);
  }

  const handleClose = () => {
    setOpen(false);
  }

  return (
    <>
      <h1> Workout Timer</h1>
      <div className='center-align' style={{ margin: "0em 33.3em" }}>
        <div style={{ height: "15em", width: "25em", backgroundColor: "red", textAlign: "center", display: "flex", flexDirection: "column", justifyContent: "space-evenly" }}>
          <div>
            <i>{num} sets of {work}s workout with {rest}s rest in between</i>
          </div>
          <b style={{ fontSize: "2.5em" }}>{minutes} : {seconds}</b>
          <div style={{ height: "20%", display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
            <button style={{ alignSelf: "center", width: "5em" }} onClick={startTimer}>Start</button><button style={{ alignSelf: "center", width: "5em" }} onClick={startTimer}>Start</button>
            <div style={{ position: "relative", bottom: "0px", width: "100%", height: "1em", textAlign: "right" }}>
              <img style={{
                width: "1.4em",
                marginRight: "3em"
              }} src="https://icons.veryicon.com/png/o/miscellaneous/ionicons-1/ios-settings-5.png"
                alt="settings"
                onClick={openModal}>
              </img>


              <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Subscribe</DialogTitle>
                <DialogContent>
                  <DialogContentText>
                    To subscribe to this website, please enter your email address here. We
                    will send updates occasionally.
                  </DialogContentText>
                  <TextField
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Email Address"
                    type="email"
                    fullWidth
                    variant="standard"
                  />
                </DialogContent>
                <DialogActions>
                  <Button onClick={handleClose}>Cancel</Button>
                  <Button onClick={handleClose}>Subscribe</Button>
                </DialogActions>
              </Dialog>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
