export const secondsToMinutes = (sec) => {
  const mins = Math.floor(sec / 60);
  const secs = Math.ceil(sec % 60);

  return {
    "mins": Number(mins),
    "secs": Number(secs)
  }
}

export const padding = (num) => {
  if (num === 0) {
    return String("00");
  }
  if (num < 10) {
    return "0" + num;
  }
  return String(num);
}
