import { padding } from "./helpers/helpers";

const Timer = () => {
  let [timeoutID, setTimeoutID] = useState(0);
  let [minutes, setMinutes] = useState(0);
  let [seconds, setSeconds] = useState(30);
  const [timerStart, setTimerStart] = useState(true);

  const startTimer = () => {
    if (numSets == totalSets) {
      timeoutID = setTimeout(() => {
        setTimerStart(true);
      }, 3500);
      setTimeoutID(timeoutID);
    } else {
      setTimerStart(true);
    }
    return stopTimer; // this is important?
  }

  /*
   * Timer Countdowns
   */
  useEffect(() => {
    let interval = 0;
    if (timerStart) {
      interval = window.setInterval(() => {
        /**
         * Setting Minutes on the Timer
         */
        let grabMinutes = Number(minutes);
        /**
         * Setting Seconds on the Timer
         */
        let grabSeconds = Number(seconds);
        if (grabSeconds === 0 && grabMinutes > 0) {
          grabMinutes -= 1;
          setMinutes(grabMinutes);
          grabSeconds = 60;
          setSeconds(grabSeconds);
        }
        setSeconds(grabSeconds - 1);
      }, 1000);
      remainingThreeSeconds();
    }
    return () => clearInterval(interval);
  }, [timerStart, seconds]);

  return (
    <>
      <div className="time">
        {mins}:{secs}
      </div>
      <b style={{ fontSize: "2.5em" }}>{padding(minutes)} : {padding(seconds)}</b>
    </>
  );
}

export default Timer;
